  src: url("https://mikumi-blog-source.netlify.app/font/FiraCode-Medium.woff2") format('woff2'), url("https://mikumi-blog-source.netlify.app/font/FiraCode-Medium.woff") format("woff");
  font-weight: 500;
  font-style: normal;
}
@font-face {
  font-family: 'Fira Code';
  src: url("https://mikumi-blog-source.netlify.app/font/FiraCode-SemiBold.woff2") format('woff2'), url("https://mikumi-blog-source.netlify.app/font/FiraCode-SemiBold.woff") format("woff");
  font-weight: 600;
  font-style: normal;
}
@font-face {
  font-family: 'Fira Code';
  src: url("https://mikumi-blog-source.netlify.app/font/FiraCode-Bold.woff2") format('woff2'), url("https://mikumi-blog-source.netlify.app/font/FiraCode-Bold.woff") format("woff");
  font-weight: 700;
  font-style: normal;
}
@font-face {
  font-family: 'Fira Code VF';
  src: url("https://mikumi-blog-source.netlify.app/font/FiraCode-VF.woff2") format('woff2-variations'), url("https://mikumi-blog-source.netlify.app/font/FiraCode-VF.woff") format('woff-variations');
/* font-weight requires a range: https://developer.mozilla.org/en-US/docs/Web/CSS/CSS_Fonts/Variable_Fonts_Guide#Using_a_variable_font_font-face_changes */
  font-weight: 300 700;
  font-style: normal;
}
body {
/* 设置背景颜色 */
  margin: 0;
  background-color: #be98aa;
  background-image: radial-gradient(closest-side, #aa8ef5, rgba(235,105,78,0)), radial-gradient(closest-side, #be97aa, rgba(243,11,164,0)), radial-gradient(closest-side, #feea83, rgba(254,234,131,0)), radial-gradient(closest-side, #be97aa, rgba(170,142,245,0)), radial-gradient(closest-side, #f8c093, rgba(248,192,147,0));
  background-size: 130vmax 130vmax, 80vmax 80vmax, 90vmax 90vmax, 110vmax 110vmax, 90vmax 90vmax;
  background-position: -80vmax -80vmax, 60vmax -30vmax, 10vmax 10vmax, -30vmax -10vmax, 50vmax 50vmax;
  background-repeat: no-repeat;
}
.content.wrap {
  opacity: 0.6;
}
.post-block {
  background: rgba(255,255,255,0.5) none repeat scroll !important;
}
.site-title {
  color: #be98aa;
}
.site-subtitle {
  color: #be98aa;
}
.header {
  background: rgba(255,255,255,0.5) none repeat scroll !important;
}
.sidebar-inner {
  background: rgba(255,255,255,0.5) none repeat scroll !important;
}
.footer {
  color: #000;
}
.comments {
  background: rgba(255,255,255,0.5) none repeat scroll !important;
}
h1 {
  color: #3e3f4c;
}
h2 {
  margin: 0.5em auto;
  color: #3e3f4c;
  padding-bottom: 5px;
  font-size: 1.5rem;
}
h2::before {
  color: #be98aa;
  content: "\f70e  ";
  width: 9px;
  height: 9px;
  font-family: FontAwesome;
}
h3 {
  margin: 0.5em auto;
  color: #be98aa;
  background: #3e3f4c;
  border-left: 4px solid #be98aa;
  padding: 0.2em 0.6em;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.1);
}
h3:hover a {
  text-decoration: underline;
}
h3::after {
  content: " \f061";
  width: 9px;
  height: 9px;
  font-family: FontAwesome;
}
h4,
h5,
h6 {
  margin-top: 20px;
  padding-bottom: 5px;
  border-bottom: 1px solid #ddd;
}
h4::before {
  color: #be98aa;
  content: "\f4d8  ";
  width: 9px;
  height: 9px;
  font-family: FontAwesome;
}
h5::before {
  color: #be98aa;
  content: "\f0c2  ";
  width: 9px;
  height: 9px;
  font-family: FontAwesome;
}
h6::before {
  color: #be98aa;
  content: "\f004  ";
  width: 9px;
  height: 9px;
  font-family: FontAwesome;
}
blockquote {
  padding: 0.4em 20px;
  margin: 0.5em auto;
  border-left: 4px solid #af888a;
  background: #f1e5de;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.1);
}
blockquote p {
  margin: 0.4em auto;
}
blockquote blockquote {
  border-left: none;
}
blockquote footer {
  font-style: italic;
}
blockquote footer cite::before {
  content: "—";
  padding: 0 0.3em;
}
.btn {
  background: rgba(255,255,255,0);
  border: 2px solid #3e3f4c;
  color: #3e3f4c;
}
.btn:hover {
  background: #3e3f4c;
  border-color: #be98aa;
  color: #be98aa;
}
.post-body .tabs ul.nav-tabs,
.tabs-comment ul.nav-tabs {
  background: rgba(190,152,170,0.7);
}
.post-body .tabs ul.nav-tabs li.tab,
.tabs-comment ul.nav-tabs li.tab {
  border-bottom: 1px solid #3e3f4c;
  border-left: 1px solid #3e3f4c;
  border-right: 1px solid #3e3f4c;
  border-top: 3px solid #3e3f4c;
}
li.tab.active {
  background: rgba(241,229,222,0.7);
}

