    border-radius: 6px;
    background: var(--waline-bgcolor);
    box-shadow: var(--waline-box-shadow);
    opacity: 0;
    visibility: hidden;
    transition: transform .2s ease-out, opacity .2s ease-out;
    transform: scale(0.9, 0.9);
    transform-origin: 0 0
}

.wl-gif-popup.display {
