.wl-card .wl-content {
    position: relative;
    margin-bottom: .75em;
    padding-top: .625em;
    font-size: .875em;
    line-height: 2;
    word-wrap: break-word
}

.wl-card .wl-content.expand {
    overflow: hidden;
    max-height: 8em;
    cursor: pointer
}

.wl-card .wl-content.expand::before {
    content: "";
    position: absolute;
    top: 0;
    bottom: 3.15em;
    inset-inline-start: 0;
    z-index: 999;
