    text-align: end
}

.wl-meta-head {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: .375em
}

.wl-sort {
    margin: 0;
    list-style-type: none
}

.wl-sort li {
    display: inline-block;
    color: var(--waline-info-color);
    font-size: .75em;
    cursor: pointer
}

.wl-sort li.active {
    color: var(--waline-theme-color)
}

.wl-sort li+li {
    margin-inline-start: 1em
}

.wl-reaction {
    overflow: auto hidden;
    margin-bottom: 1.75em;
    text-align: center
}

.wl-reaction img {
    width: 100%;
    height: 100%;
    transition: all 250ms ease-in-out
}

.wl-reaction-title {
    margin: 16px auto;
    font-weight: bold;
    font-size: 18px
}

.wl-reaction-list {
    display: flex;
    flex-direction: row;
    gap: 16px;
    justify-content: center;
    margin: 0;
    padding: 8px;
    list-style-type: none
}

@media(max-width: 580px) {
    .wl-reaction-list {
        gap: 12px
    }
}

[data-waline] .wl-reaction-list {
    margin-inline-start: 0
}

.wl-reaction-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer
}

.wl-reaction-item:hover img,
.wl-reaction-item.active img {
    transform: scale(1.15)
}

.wl-reaction-img {
    position: relative;
    width: 42px;
    height: 42px
}

@media(max-width: 580px) {
    .wl-reaction-img {
        width: 32px;
        height: 32px
    }
}

.wl-reaction-loading {
    position: absolute;
    top: -4px;
    inset-inline-end: -5px;
    width: 18px;
    height: 18px;
    color: var(--waline-theme-color)
}

.wl-reaction-votes {
    position: absolute;
    top: -9px;
    inset-inline-end: -9px;
    min-width: 1em;
    padding: 2px;
    border: 1px solid var(--waline-theme-color);
    border-radius: 1em;
    background: var(--waline-bgcolor);
    color: var(--waline-theme-color);
    font-weight: 700;
    font-size: .75em;
    line-height: 1
}

.wl-reaction-item.active .wl-reaction-votes {
    background: var(--waline-theme-color);
    color: var(--waline-bgcolor)
}

.wl-reaction-text {
    font-size: .875em
}

.wl-reaction-item.active .wl-reaction-text {
    color: var(--waline-theme-color)
}

.wl-content pre,
.wl-content pre[class*=language-] {
    overflow: auto;
    margin: .75rem 0;
    padding: 1rem 1.25rem;
    border-radius: 6px;
    background: var(--waline-code-bgcolor);
    line-height: 1.4
}

.wl-content pre code,
.wl-content pre[class*=language-] code {
    padding: 0;
    border-radius: 0;
    background: rgba(0, 0, 0, 0) !important;
    color: #bbb;
    direction: ltr
}

.wl-content code[class*=language-],
.wl-content pre[class*=language-] {
    background: none;
    color: #ccc;
    font-size: 1em;
    font-family: Consolas, Monaco, "Andale Mono", "Ubuntu Mono", monospace;
    text-align: left;
    white-space: pre;
    word-spacing: normal;
    word-wrap: normal;
    word-break: normal;
    tab-size: 4;
    hyphens: none
}

.wl-content pre[class*=language-] {
    overflow: auto
}

.wl-content :not(pre)>code[class*=language-],
.wl-content pre[class*=language-] {
    background: #2d2d2d
}

.wl-content :not(pre)>code[class*=language-] {
    padding: .1em;
    border-radius: .3em;
    white-space: normal
}

.wl-content .token.comment,
.wl-content .token.block-comment,
.wl-content .token.prolog,
.wl-content .token.doctype,
.wl-content .token.cdata {
    color: #999
}

.wl-content .token.punctuation {
    color: #ccc
}

.wl-content .token.tag,
.wl-content .token.attr-name,
.wl-content .token.namespace,
.wl-content .token.deleted {
    color: #e2777a
}

.wl-content .token.function-name {
    color: #6196cc
}

.wl-content .token.boolean,
.wl-content .token.number,
.wl-content .token.function {
    color: #f08d49
}

.wl-content .token.property,
.wl-content .token.class-name,
.wl-content .token.constant,
.wl-content .token.symbol {
    color: #f8c555
}

.wl-content .token.selector,
.wl-content .token.important,
.wl-content .token.atrule,
.wl-content .token.keyword,
.wl-content .token.builtin {
