    height: 100%;
    border-radius: var(--waline-avatar-radius)
}

.wl-login-nick {
    display: block;
    color: var(--waline-theme-color);
    font-size: .75em;
    word-break: break-all
}

.wl-panel {
    position: relative;
    flex-shrink: 1;
    width: 100%;
    margin: .5em;
    border: var(--waline-border);
    border-radius: .75em;
    background: var(--waline-bgcolor);
    box-shadow: var(--waline-box-shadow)
}

.wl-header {
    display: flex;
    overflow: hidden;
    padding: 0 4px;
    border-bottom: 2px dashed var(--waline-border-color);
    border-top-left-radius: .75em;
    border-top-right-radius: .75em
}

@media(max-width: 580px) {
    .wl-header {
        display: block
    }
}

.wl-header label {
    min-width: 40px;
    padding: .75em .5em;
    color: var(--waline-color);
    font-size: .75em;
    text-align: center
}

.wl-header input {
    flex: 1;
    width: 0;
    padding: .5em;
    background: rgba(0, 0, 0, 0);
    font-size: .625em;
    resize: none
}

.wl-header-item {
    display: flex;
    flex: 1
}

@media(max-width: 580px) {
    .wl-header-item:not(:last-child) {
        border-bottom: 2px dashed var(--waline-border-color)
    }
}

.wl-header-1 .wl-header-item {
    width: 100%
}

.wl-header-2 .wl-header-item {
    width: 50%
}

@media(max-width: 580px) {
    .wl-header-2 .wl-header-item {
        flex: 0;
        width: 100%
    }
}

.wl-header-3 .wl-header-item {
    width: 33.33%
}

@media(max-width: 580px) {
    .wl-header-3 .wl-header-item {
        width: 100%
    }
}

.wl-editor {
    position: relative;
    width: calc(100% - 1em);
    min-height: 8.75em;
    margin: .75em .5em;
    border-radius: .5em;
    background: rgba(0, 0, 0, 0);
    font-size: .875em;
    resize: vertical
}

.wl-editor,
.wl-input {
    max-width: 100%;
    border: none;
    color: var(--waline-color);
    outline: none;
    transition: all .25s ease
}

.wl-editor:focus,
.wl-input:focus {
    background: var(--waline-bgcolor-light)
}

.wl-preview {
    padding: 0 .5em .5em
}

.wl-preview h4 {
    margin: .25em;
    font-weight: bold;
    font-size: .9375em
}

.wl-preview .wl-content {
    min-height: 1.25em;
    padding: .25em;
    word-break: break-word;
    hyphens: auto
}

.wl-preview .wl-content>*:first-child {
    margin-top: 0
}

.wl-preview .wl-content>*:last-child {
    margin-bottom: 0
}

.wl-footer {
    position: relative;
    display: flex;
    flex-wrap: wrap;
    margin: .5em .75em
}

.wl-actions {
    display: flex;
    flex: 2;
    align-items: center
}

.wl-action {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 1.5em;
    height: 1.5em;
    margin: 2px;
    padding: 0;
    border: none;
    background: rgba(0, 0, 0, 0);
    color: var(--waline-color);
    font-size: 16px;
    cursor: pointer
}

.wl-action:hover {
    color: var(--waline-theme-color)
}

.wl-action.active {
    color: var(--waline-active-color)
}

#wl-image-upload {
    display: none
}

#wl-image-upload:focus+label {
    color: var(--waline-color)
}

#wl-image-upload:focus-visible+label {
    outline: -webkit-focus-ring-color auto 1px
}

.wl-info {
    display: flex;
    flex: 3;
    align-items: center;
    justify-content: flex-end
}

.wl-info .wl-text-number {
    color: var(--waline-info-color);
    font-size: .75em
}

.wl-info .wl-text-number .illegal {
    color: red
}

.wl-info button {
    margin-inline-start: .75em
}

.wl-info button svg {
    display: block;
    margin: 0 auto;
    line-height: 18px
}

.wl-emoji-popup {
    position: absolute;
    top: 100%;
    inset-inline-start: 1.25em;
    z-index: 10;
    max-width: 526px;
    border: var(--waline-border);
    border-radius: 6px;
    background: var(--waline-bgcolor);
    box-shadow: var(--waline-box-shadow);
    opacity: 0;
    visibility: hidden;
    transition: transform .2s ease-out, opacity .2s ease-out;
    transform: scale(0.9, 0.9);
    transform-origin: 0 0
}

.wl-emoji-popup.display {
    opacity: 1;
    visibility: visible;
    transform: none
}

.wl-emoji-popup button {
    display: inline-block;
    vertical-align: middle;
    width: 2em;
    margin: .125em;
    padding: 0;
    border-width: 0;
    background: rgba(0, 0, 0, 0);
    font-size: inherit;
    line-height: 2;
    text-align: center;
    cursor: pointer
}

.wl-emoji-popup button:hover {
    background: var(--waline-bgcolor-hover)
}

.wl-emoji-popup .wl-emoji {
    display: inline-block;
    vertical-align: middle;
    max-width: 1.5em;
    max-height: 1.5em
}

.wl-emoji-popup .wl-tab-wrapper {
    overflow-y: auto;
    max-height: 145px;
    padding: .5em
}

.wl-emoji-popup .wl-tab-wrapper::-webkit-scrollbar {
    width: 6px;
    height: 6px
}

.wl-emoji-popup .wl-tab-wrapper::-webkit-scrollbar-track-piece:vertical {
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: rgba(0, 0, 0, .1)
}

.wl-emoji-popup .wl-tab-wrapper::-webkit-scrollbar-thumb:vertical {
    width: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: var(--waline-theme-color)
}

.wl-emoji-popup .wl-tabs {
    position: relative;
    overflow-x: auto;
    padding: 0 6px;
    white-space: nowrap
}

.wl-emoji-popup .wl-tabs::before {
    content: " ";
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    z-index: 2;
    height: 1px;
    background: var(--waline-border-color)
}

.wl-emoji-popup .wl-tabs::-webkit-scrollbar {
    width: 6px;
    height: 6px
}

.wl-emoji-popup .wl-tabs::-webkit-scrollbar-track-piece:horizontal {
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: rgba(0, 0, 0, .1)
}

.wl-emoji-popup .wl-tabs::-webkit-scrollbar-thumb:horizontal {
    height: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: var(--waline-theme-color)
}

.wl-emoji-popup .wl-tab {
    position: relative;
    margin: 0;
    padding: 0 .5em
}

.wl-emoji-popup .wl-tab.active {
    z-index: 3;
    border: 1px solid var(--waline-border-color);
    border-top-width: 0;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
    background: var(--waline-bgcolor)
}

.wl-gif-popup {
    position: absolute;
    top: 100%;
    inset-inline-start: 1.25em;
    z-index: 10;
    width: calc(100% - 3em);
    padding: .75em .75em .25em;
    border: var(--waline-border);
    border-radius: 6px;
    background: var(--waline-bgcolor);
    box-shadow: var(--waline-box-shadow);
    opacity: 0;
    visibility: hidden;
    transition: transform .2s ease-out, opacity .2s ease-out;
    transform: scale(0.9, 0.9);
    transform-origin: 0 0
}

.wl-gif-popup.display {
    opacity: 1;
    visibility: visible;
    transform: none
}

.wl-gif-popup input {
    box-sizing: border-box;
    width: 100%;
    margin-bottom: 10px;
    padding: 3px 5px;
    border: var(--waline-border)
}

.wl-gif-popup img {
    display: block;
    box-sizing: border-box;
    width: 100%;
    border-width: 2px;
    border-style: solid;
    border-color: #fff;
    cursor: pointer
}

.wl-gif-popup img:hover {
    border-color: var(--waline-theme-color);
    border-radius: 2px
}

.wl-gallery {
    display: flex;
    overflow-y: auto;
    max-height: 80vh
}

.wl-gallery-column {
    display: flex;
    flex: 1;
    flex-direction: column;
    height: -webkit-max-content;
    height: -moz-max-content;
    height: max-content
}

.wl-cards .wl-user {
    --avatar-size: var(--waline-avatar-size);
    position: relative;
    margin-inline-end: .75em
}

@media(max-width: 720px) {
    .wl-cards .wl-user {
        --avatar-size: var(--waline-m-avatar-size)
    }
}

.wl-cards .wl-user img {
    width: var(--avatar-size);
    height: var(--avatar-size);
    border-radius: var(--waline-avatar-radius);
    box-shadow: var(--waline-box-shadow)
}

.wl-cards .wl-user .verified-icon {
    position: absolute;
    top: calc(var(--avatar-size)*3/4);
    inset-inline-start: calc(var(--avatar-size)*3/4);
    border-radius: 50%;
    background: var(--waline-bgcolor);
    box-shadow: var(--waline-box-shadow)
}

.wl-card-item {
    position: relative;
    display: flex;
    padding: .5em
}

.wl-card-item .wl-card-item {
    padding-inline-end: 0
}

.wl-card {
    flex: 1;
    width: 0;
    padding-bottom: .5em;
    border-bottom: 1px dashed var(--waline-border-color)
}

.wl-card:first-child {
    margin-inline-start: 1em
}

.wl-card-item:last-child>.wl-card {
    border-bottom: none
}

.wl-card .wl-nick svg {
    position: relative;
    bottom: -0.125em;
    line-height: 1
}

.wl-card .wl-head {
    overflow: hidden;
    line-height: 1.5
}

.wl-card .wl-head .wl-nick {
    position: relative;
    display: inline-block;
    margin-inline-end: .5em;
    font-weight: bold;
    font-size: .875em;
    line-height: 1;
    text-decoration: none
}

.wl-card span.wl-nick {
    color: var(--waline-dark-grey)
}

.wl-card .wl-badge {
    display: inline-block;
    margin-inline-end: 1em;
    padding: 0 .3em;
    border: 1px solid var(--waline-badge-color);
    border-radius: 4px;
    color: var(--waline-badge-color);
    font-size: var(--waline-badge-font-size)
}

.wl-card .wl-time {
    margin-inline-end: .875em;
    color: var(--waline-info-color);
    font-size: .75em
}

.wl-card .wl-meta {
    position: relative;
    line-height: 1
}

.wl-card .wl-meta>span {
    display: inline-block;
    margin-inline-end: .25em;
    padding: 2px 4px;
    border-radius: .2em;
    background: var(--waline-info-bgcolor);
    color: var(--waline-info-color);
    font-size: var(--waline-info-font-size);
    line-height: 1.5
}

.wl-card .wl-meta>span:empty {
    display: none
}

.wl-card .wl-comment-actions {
    float: right;
    line-height: 1
}

[dir=rtl] .wl-card .wl-comment-actions {
    float: left
}

.wl-card .wl-delete,
.wl-card .wl-like,
.wl-card .wl-reply,
.wl-card .wl-edit {
    display: inline-flex;
    align-items: center;
    border: none;
    background: rgba(0, 0, 0, 0);
    color: var(--waline-color);
    line-height: 1;
    cursor: pointer;
    transition: color .2s ease
}

.wl-card .wl-delete:hover,
.wl-card .wl-like:hover,
.wl-card .wl-reply:hover,
.wl-card .wl-edit:hover {
    color: var(--waline-theme-color)
}

.wl-card .wl-delete.active,
.wl-card .wl-like.active,
.wl-card .wl-reply.active,
.wl-card .wl-edit.active {
    color: var(--waline-active-color)
}

.wl-card .wl-content {
    position: relative;
    margin-bottom: .75em;
    padding-top: .625em;
    font-size: .875em;
    line-height: 2;
    word-wrap: break-word
}

.wl-card .wl-content.expand {
    overflow: hidden;
    max-height: 8em;
    cursor: pointer
}

.wl-card .wl-content.expand::before {
    content: "";
    position: absolute;
    top: 0;
    bottom: 3.15em;
    inset-inline-start: 0;
    z-index: 999;
    display: block;
    width: 100%;
    background: linear-gradient(180deg, #000, rgba(255, 255, 255, 0.9))
}

.wl-card .wl-content.expand::after {
    content: attr(data-expand);
    position: absolute;
    bottom: 0;
    inset-inline-start: 0;
    z-index: 999;
    display: block;
    width: 100%;
    height: 3.15em;
    background: rgba(255, 255, 255, .9);
    color: #828586;
    line-height: 3.15em;
    text-align: center
}

.wl-card .wl-content>*:first-child {
    margin-top: 0
}

.wl-card .wl-content>*:last-child {
    margin-bottom: 0
}

.wl-card .wl-admin-actions {
    margin: 8px 0;
    font-size: 12px;
    text-align: right
}

.wl-card .wl-comment-status {
    margin: 0 8px
}

.wl-card .wl-comment-status .wl-btn {
    border-radius: 0
}

.wl-card .wl-comment-status .wl-btn:first-child {
    border-inline-end: 0;
    border-radius: .5em 0 0 .5em
}

.wl-card .wl-comment-status .wl-btn:last-child {
    border-inline-start: 0;
    border-radius: 0 .5em .5em 0
}

.wl-card .wl-quote {
    border-inline-start: 1px dashed rgba(237, 237, 237, .5)
}

.wl-card .wl-quote .wl-user {
