}

.wl-close-icon {
    color: var(--waline-border-color)
}

.wl-content .vemoji,
.wl-content .wl-emoji {
    display: inline-block;
    vertical-align: baseline;
    height: 1.25em;
    margin: -0.125em .25em
}

.wl-content .wl-tex {
    background: var(--waline-info-bgcolor);
    color: var(--waline-info-color)
}

.wl-content span.wl-tex {
