    margin-inline-end: .25em;
    padding: 2px 4px;
    border-radius: .2em;
    font-size: var(--waline-info-font-size);
    line-height: 1.5
}

.wl-content p.wl-tex {
    text-align: center
}

.wl-content .katex-display {
    overflow: auto hidden;
    -webkit-overflow-scrolling: touch;
    padding-top: .2em;
    padding-bottom: .2em
}

.wl-content .katex-display::-webkit-scrollbar {
    height: 3px
}

.wl-content .katex-error {
