}

.wl-content .token.italic {
    font-style: italic
}

.wl-content .token.entity {
    cursor: help
}

.wl-content .token.inserted {
    color: green
}

.wl-recent-item p {
    display: inline
}

.wl-user-list {
    padding: 0;
    list-style: none
}

.wl-user-list a,
.wl-user-list a:hover,
.wl-user-list a:visited {
