Page.more:
:root {
    --waline-font-size: 1rem;
    --waline-white: #fff;
    --waline-light-grey: #999;
    --waline-dark-grey: #666;
    --waline-theme-color: #e67372;
    --waline-active-color: #698e6c;
    --waline-color: #444;
    --waline-bgcolor: #fff;
    --waline-bgcolor-light: #f8f8f8;
    --waline-bgcolor-hover: #f0f0f0;
    --waline-border-color: #ddd;
    --waline-disable-bgcolor: #f8f8f8;
    --waline-disable-color: #000;
    --waline-code-bgcolor: #282c34;
    --waline-bq-color: #f0f0f0;
    --waline-avatar-size: 3.25rem;
    --waline-m-avatar-size: calc(var(--waline-avatar-size) * 9 / 13);
    --waline-badge-color: #698e6c;
    --waline-badge-font-size: 0.75em;
    --waline-info-bgcolor: #f8f8f8;
    --waline-info-color: #999;
    --waline-info-font-size: 0.625em;
    --waline-border: 1px solid var(--waline-border-color);
    --waline-avatar-radius: 50%;
    --waline-box-shadow: none
}

[data-waline] {
    font-size: var(--waline-font-size);
    text-align: start
}

[dir=rtl] [data-waline] {
    direction: rtl
}

[data-waline] * {
    box-sizing: content-box;
    line-height: 1.75
}

[data-waline] p {
    color: var(--waline-color)
}

[data-waline] a {
    position: relative;
    display: inline-block;
    color: var(--waline-theme-color);
    text-decoration: none;
    word-break: break-word;
    cursor: pointer
}

[data-waline] a:hover {
    color: var(--waline-active-color)
}

[data-waline] img {
    max-width: 100%;
    max-height: 400px;
    border: none
}

[data-waline] hr {
    margin: .825em 0;
    border-style: dashed;
    border-color: var(--waline-bgcolor-light)
}

[data-waline] code,
[data-waline] pre {
    margin: 0;
    padding: .2em .4em;
    border-radius: 3px;
    background: var(--waline-bgcolor-light);
    font-size: 85%
}

[data-waline] pre {
    overflow: auto;
    padding: 10px;
    line-height: 1.45
}

[data-waline] pre::-webkit-scrollbar {
    width: 6px;
    height: 6px
}

[data-waline] pre::-webkit-scrollbar-track-piece:horizontal {
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: rgba(0, 0, 0, .1)
}

[data-waline] pre::-webkit-scrollbar-thumb:horizontal {
    width: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: var(--waline-theme-color)
}

[data-waline] pre code {
    padding: 0;
    background: rgba(0, 0, 0, 0);
    color: var(--waline-color);
    white-space: pre-wrap;
    word-break: keep-all
}

[data-waline] blockquote {
    margin: .5em 0;
    padding: .5em 0 .5em 1em;
    border-inline-start: 8px solid var(--waline-bq-color);
    color: var(--waline-dark-grey)
}

[data-waline] blockquote>p {
    margin: 0
}

[data-waline] ol,
[data-waline] ul {
    margin-inline-start: 1.25em;
    padding: 0
}

[data-waline] input[type=checkbox],
[data-waline] input[type=radio] {
    display: inline-block;
    vertical-align: middle;
    margin-top: -2px
}

.wl-btn {
    display: inline-block;
    vertical-align: middle;
    min-width: 2.5em;
    margin-bottom: 0;
    padding: .5em 1em;
    border: 1px solid var(--waline-border-color);
    border-radius: .5em;
    background: rgba(0, 0, 0, 0);
    color: var(--waline-color);
    font-weight: 400;
    font-size: .75em;
    line-height: 1.5;
    text-align: center;
    white-space: nowrap;
    cursor: pointer;
    user-select: none;
    transition-duration: .4s;
    touch-action: manipulation
}

.wl-btn:hover,
.wl-btn:active {
    border-color: var(--waline-theme-color);
    color: var(--waline-theme-color)
}

.wl-btn:disabled {
    border-color: var(--waline-border-color);
    background: var(--waline-disable-bgcolor);
    color: var(--waline-disable-color);
    cursor: not-allowed
}

.wl-btn.primary {
    border-color: var(--waline-theme-color);
    background: var(--waline-theme-color);
    color: var(--waline-white)
}

.wl-btn.primary:hover,
.wl-btn.primary:active {
    border-color: var(--waline-active-color);
    background: var(--waline-active-color);
    color: var(--waline-white)
}

.wl-btn.primary:disabled {
    border-color: var(--waline-border-color);
    background: var(--waline-disable-bgcolor);
    color: var(--waline-disable-color);
    cursor: not-allowed
}

.wl-loading {
    text-align: center
}

.wl-loading svg {
    margin: 0 auto
}

.wl-comment {
    position: relative;
    display: flex;
    margin-bottom: .75em
}

.wl-close {
    position: absolute;
    top: -4px;
    inset-inline-end: -4px;
    padding: 0;
    border: none;
    background: rgba(0, 0, 0, 0);
    line-height: 1;
    cursor: pointer
}

.wl-login-info {
    max-width: 80px;
    margin-top: .75em;
    text-align: center
}

.wl-logout-btn {
    position: absolute;
    top: -10px;
    inset-inline-end: -10px;
    padding: 3px;
    border: none;
    background: rgba(0, 0, 0, 0);
    line-height: 0;
    cursor: pointer
}

.wl-avatar {
    position: relative;
    width: var(--waline-avatar-size);
    height: var(--waline-avatar-size);
    margin: 0 auto;
    border: var(--waline-border);
    border-radius: var(--waline-avatar-radius)
}

@media(max-width: 720px) {
    .wl-avatar {
        width: var(--waline-m-avatar-size);
        height: var(--waline-m-avatar-size)
    }
}

.wl-avatar img {
    width: 100%;
    height: 100%;
    border-radius: var(--waline-avatar-radius)
}

.wl-login-nick {
    display: block;
    color: var(--waline-theme-color);
    font-size: .75em;
    word-break: break-all
}

.wl-panel {
    position: relative;
    flex-shrink: 1;
    width: 100%;
    margin: .5em;
    border: var(--waline-border);
    border-radius: .75em;
    background: var(--waline-bgcolor);
    box-shadow: var(--waline-box-shadow)
}

.wl-header {
    display: flex;
    overflow: hidden;
    padding: 0 4px;
    border-bottom: 2px dashed var(--waline-border-color);
    border-top-left-radius: .75em;
    border-top-right-radius: .75em
}

@media(max-width: 580px) {
    .wl-header {
        display: block
    }
}

.wl-header label {
    min-width: 40px;
    padding: .75em .5em;
    color: var(--waline-color);
    font-size: .75em;
    text-align: center
}

.wl-header input {
    flex: 1;
    width: 0;
    padding: .5em;
    background: rgba(0, 0, 0, 0);
    font-size: .625em;
    resize: none
}

.wl-header-item {
    display: flex;
    flex: 1
}

@media(max-width: 580px) {
    .wl-header-item:not(:last-child) {
        border-bottom: 2px dashed var(--waline-border-color)
    }
}

.wl-header-1 .wl-header-item {
    width: 100%
}

.wl-header-2 .wl-header-item {
    width: 50%
}

@media(max-width: 580px) {
    .wl-header-2 .wl-header-item {
        flex: 0;
        width: 100%
    }
}

.wl-header-3 .wl-header-item {
    width: 33.33%
}

@media(max-width: 580px) {
    .wl-header-3 .wl-header-item {
        width: 100%
    }
}

.wl-editor {
    position: relative;
    width: calc(100% - 1em);
    min-height: 8.75em;
    margin: .75em .5em;
    border-radius: .5em;
    background: rgba(0, 0, 0, 0);
    font-size: .875em;
    resize: vertical
}

.wl-editor,
.wl-input {
    max-width: 100%;
    border: none;
    color: var(--waline-color);
    outline: none;
    transition: all .25s ease
}

.wl-editor:focus,
.wl-input:focus {
    background: var(--waline-bgcolor-light)
}

.wl-preview {
    padding: 0 .5em .5em
}

.wl-preview h4 {
    margin: .25em;
    font-weight: bold;
    font-size: .9375em
}

.wl-preview .wl-content {
    min-height: 1.25em;
    padding: .25em;
    word-break: break-word;
    hyphens: auto
}

.wl-preview .wl-content>*:first-child {
    margin-top: 0
}

.wl-preview .wl-content>*:last-child {
    margin-bottom: 0
}

.wl-footer {
    position: relative;
    display: flex;
    flex-wrap: wrap;
    margin: .5em .75em
}

.wl-actions {
    display: flex;
    flex: 2;
    align-items: center
}

.wl-action {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 1.5em;
    height: 1.5em;
    margin: 2px;
    padding: 0;
    border: none;
    background: rgba(0, 0, 0, 0);
    color: var(--waline-color);
    font-size: 16px;
    cursor: pointer
}

.wl-action:hover {
    color: var(--waline-theme-color)
}

.wl-action.active {
    color: var(--waline-active-color)
}

#wl-image-upload {
    display: none
}

#wl-image-upload:focus+label {
    color: var(--waline-color)
}

#wl-image-upload:focus-visible+label {
    outline: -webkit-focus-ring-color auto 1px
}

.wl-info {
    display: flex;
    flex: 3;
    align-items: center;
    justify-content: flex-end
}

.wl-info .wl-text-number {
    color: var(--waline-info-color);
    font-size: .75em
}

.wl-info .wl-text-number .illegal {
    color: red
}

.wl-info button {
    margin-inline-start: .75em
}

.wl-info button svg {
    display: block;
    margin: 0 auto;
    line-height: 18px
}

.wl-emoji-popup {
    position: absolute;
    top: 100%;
    inset-inline-start: 1.25em;
    z-index: 10;
    max-width: 526px;
    border: var(--waline-border);
    border-radius: 6px;
    background: var(--waline-bgcolor);
    box-shadow: var(--waline-box-shadow);
    opacity: 0;
    visibility: hidden;
    transition: transform .2s ease-out, opacity .2s ease-out;
    transform: scale(0.9, 0.9);
    transform-origin: 0 0
}

.wl-emoji-popup.display {
    opacity: 1;
    visibility: visible;
    transform: none
}

.wl-emoji-popup button {
    display: inline-block;
    vertical-align: middle;
    width: 2em;
    margin: .125em;
    padding: 0;
    border-width: 0;
    background: rgba(0, 0, 0, 0);
    font-size: inherit;
    line-height: 2;
    text-align: center;
    cursor: pointer
}

.wl-emoji-popup button:hover {
    background: var(--waline-bgcolor-hover)
}

.wl-emoji-popup .wl-emoji {
    display: inline-block;
    vertical-align: middle;
    max-width: 1.5em;
    max-height: 1.5em
}

.wl-emoji-popup .wl-tab-wrapper {
    overflow-y: auto;
    max-height: 145px;
    padding: .5em
}

.wl-emoji-popup .wl-tab-wrapper::-webkit-scrollbar {
    width: 6px;
    height: 6px
}

.wl-emoji-popup .wl-tab-wrapper::-webkit-scrollbar-track-piece:vertical {
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: rgba(0, 0, 0, .1)
}

.wl-emoji-popup .wl-tab-wrapper::-webkit-scrollbar-thumb:vertical {
    width: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: var(--waline-theme-color)
}

.wl-emoji-popup .wl-tabs {
    position: relative;
    overflow-x: auto;
    padding: 0 6px;
    white-space: nowrap
}

.wl-emoji-popup .wl-tabs::before {
    content: " ";
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    z-index: 2;
    height: 1px;
    background: var(--waline-border-color)
}

.wl-emoji-popup .wl-tabs::-webkit-scrollbar {
    width: 6px;
    height: 6px
}

.wl-emoji-popup .wl-tabs::-webkit-scrollbar-track-piece:horizontal {
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background: rgba(0, 0, 0, .1)
}

.wl-emoji-popup .wl-tabs::-webkit-scrollbar-thumb:horizontal {
