    width: 100%;
    margin-bottom: 10px;
    padding: 3px 5px;
    border: var(--waline-border)
}

.wl-gif-popup img {
    display: block;
    box-sizing: border-box;
    width: 100%;
    border-width: 2px;
    border-style: solid;
    border-color: #fff;
    cursor: pointer
}

.wl-gif-popup img:hover {
    border-color: var(--waline-theme-color);
    border-radius: 2px
}

.wl-gallery {
    display: flex;
    overflow-y: auto;
