}

.wl-card .wl-nick svg {
    position: relative;
    bottom: -0.125em;
    line-height: 1
}

.wl-card .wl-head {
    overflow: hidden;
    line-height: 1.5
}

.wl-card .wl-head .wl-nick {
    position: relative;
    display: inline-block;
    margin-inline-end: .5em;
    font-weight: bold;
    font-size: .875em;
    line-height: 1;
    text-decoration: none
}

.wl-card span.wl-nick {
    color: var(--waline-dark-grey)
}

.wl-card .wl-badge {
    display: inline-block;
    margin-inline-end: 1em;
    padding: 0 .3em;
    border: 1px solid var(--waline-badge-color);
